        <!-- Disable Webflow's built-in smooth scroll, Add custom smooth scroll with offset. -->
        <script>
            // Disable Webflow's built-in smooth scrolling
            var Webflow = Webflow || [];
            Webflow.push(function() {
                $(function() {
                    $(document).off('click.wf-scroll');
                });
            });

            document.addEventListener("DOMContentLoaded", function() {
                const headerOffset = 96;

                // Smooth scroll function
                function smoothScroll(target) {
                    const element = document.getElementById(target);
                    if (element) {
                        const elementPosition = element.getBoundingClientRect().top + window.pageYOffset;
                        const offsetPosition = elementPosition - headerOffset;

                        window.scrollTo({
                            top: offsetPosition,
                            behavior: 'smooth'
                        });
                    }
                }

                // Select all links with href starting with '#' and filter
                const links = Array.from(document.querySelectorAll('a[href^="#"]')).filter(link => {
                    const href = link.getAttribute('href').trim();
                    return href.length > 1 && href[1] !== ' ';
                    // Ensure at least one non-space character after #
                }
                );

                // Add click event listeners for smooth scrolling
                links.forEach(anchor => {
                    anchor.addEventListener('click', function(e) {
                        e.preventDefault();
                        const targetId = this.getAttribute('href').substring(1);
                        smoothScroll(targetId);
                    });
                }
                );

                // Smooth scroll on page load if there's a hash
                function handleHashChange() {
                    if (window.location.hash) {
                        const targetId = window.location.hash.substring(1);
                        if (targetId.trim()) {
                            // Ensure there's a non-space character
                            smoothScroll(targetId);
                        }
                    }
                }
                handleHashChange();
                window.addEventListener('hashchange', handleHashChange);
            });
        </script>
